CREATE TABLE kejahatan_kota_bandung(
   id                      INTEGER  NOT NULL PRIMARY KEY
  ,bps_kode_kecamatan      INTEGER  NOT NULL
  ,bps_nama_kecamatan      VARCHAR(15) NOT NULL
  ,bps_kode_desa_kelurahan INTEGER  NOT NULL
  ,bps_desa_kelurahan      VARCHAR(23) NOT NULL
  ,kategori_pelanggaran    VARCHAR(39) NOT NULL
  ,tahun                   INTEGER  NOT NULL
  ,latitude_kecamatan      DOUBLE NOT NULL
  ,longitude_kecamatan     DOUBLE NOT NULL
  ,latitude_desa           DOUBLE NOT NULL
  ,longitude_desa          DOUBLE NOT NULL
);
